import * as R from 'ramda'

import {FETCH_PHONES_SUCCESS} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      const newValues = R.indexBy(R.prop('id'), payload) // превращает массив в объект по ключу
      return R.merge(state, newValues) // перетерает данные если они есть. аналог спреда ...state 
  }
  return state
}